import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

function CustomerDashboard() {
  const [activeTab, setActiveTab] = useState('bookings');
  const [bookings, setBookings] = useState([]);
  const [availableServices, setAvailableServices] = useState([]);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    fetchBookings();
    if (activeTab === 'services') {
      fetchAvailableServices();
    }
  }, [activeTab]);

  const fetchBookings = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await axios.get(`http://localhost:5000/api/bookings/user/bookings`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setBookings(response.data);
    } catch (err) {
      console.error('Error fetching bookings:', err);
    }
  };

  const fetchAvailableServices = async () => {
    setLoading(true);
    try {
      const response = await axios.get('http://localhost:5000/api/services/');
      setAvailableServices(response.data);
    } catch (err) {
      console.error('Error fetching services:', err);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="container">
      <div style={{ marginBottom: '30px' }}>
        <h1>üéâ Welcome, Customer!</h1>
        <div style={{ display: 'flex', gap: '20px', marginTop: '20px', flexWrap: 'wrap' }}>
          <button 
            onClick={() => setActiveTab('bookings')}
            style={{
              padding: '12px 24px',
              background: activeTab === 'bookings' ? '#667eea' : '#e0e0e0',
              color: activeTab === 'bookings' ? 'white' : '#333',
              border: 'none',
              borderRadius: '8px',
              cursor: 'pointer',
              fontWeight: '600'
            }}
          >
            üìÖ My Bookings
          </button>
          <button 
            onClick={() => setActiveTab('services')}
            style={{
              padding: '12px 24px',
              background: activeTab === 'services' ? '#667eea' : '#e0e0e0',
              color: activeTab === 'services' ? 'white' : '#333',
              border: 'none',
              borderRadius: '8px',
              cursor: 'pointer',
              fontWeight: '600'
            }}
          >
            üéØ Browse Services
          </button>
          <Link 
            to="/book-venue"
            style={{
              padding: '12px 24px',
              background: '#764ba2',
              color: 'white',
              border: 'none',
              borderRadius: '8px',
              cursor: 'pointer',
              fontWeight: '600',
              textDecoration: 'none',
              display: 'inline-block'
            }}
          >
            ‚ûï New Booking
          </Link>
        </div>
      </div>

      {activeTab === 'bookings' && (
        <div style={{ marginTop: '30px' }}>
          <h2>My Bookings</h2>
          {bookings.length === 0 ? (
            <div style={{ background: '#f5f5f5', padding: '30px', borderRadius: '8px', textAlign: 'center' }}>
              <p style={{ fontSize: '1.1rem', color: '#666' }}>No bookings yet</p>
              <Link to="/book-venue" style={{ color: '#667eea', textDecoration: 'underline' }}>Create your first booking</Link>
            </div>
          ) : (
            bookings.map(booking => (
              <div key={booking._id} style={{ background: 'white', padding: '20px', borderRadius: '8px', marginBottom: '15px', boxShadow: '0 2px 8px rgba(0,0,0,0.1)' }}>
                <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'start' }}>
                  <div>
                    <h3>{booking.venue?.name || 'Venue'}</h3>
                    <p><strong>üìÖ Date:</strong> {new Date(booking.eventDate).toDateString()}</p>
                    <p><strong>‚è∞ Time:</strong> {booking.eventStartTime} - {booking.eventEndTime}</p>
                    <p><strong>üé≠ Type:</strong> {booking.eventType}</p>
                    <p><strong>üë• Guests:</strong> {booking.guestCount}</p>
                  </div>
                  <div style={{ textAlign: 'right' }}>
                    <p style={{ fontSize: '1.2rem', fontWeight: 'bold', color: '#667eea' }}>‚Çπ{booking.totalCost}</p>
                    <p><strong>Status:</strong> <span style={{ padding: '4px 8px', borderRadius: '4px', background: booking.status === 'confirmed' ? '#e8f5e9' : booking.status === 'cancelled' ? '#ffebee' : '#fff3e0', color: booking.status === 'confirmed' ? '#2e7d32' : booking.status === 'cancelled' ? '#c62828' : '#e65100' }}>{booking.status.toUpperCase()}</span></p>
                    <p><strong>Payment:</strong> <span style={{ color: booking.paymentStatus === 'completed' ? 'green' : 'orange' }}>{booking.paymentStatus || 'Pending'}</span></p>
                  </div>
                </div>
              </div>
            ))
          )}
        </div>
      )}

      {activeTab === 'services' && (
        <div style={{ marginTop: '30px' }}>
          <h2>Available Services for Your Events</h2>
          {loading ? (
            <p>Loading services...</p>
          ) : availableServices.length === 0 ? (
            <p>No services available</p>
          ) : (
            <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fill, minmax(300px, 1fr))', gap: '20px' }}>
              {availableServices.map(service => (
                <div key={service._id} style={{ background: 'white', padding: '20px', borderRadius: '8px', boxShadow: '0 2px 8px rgba(0,0,0,0.1)', border: '1px solid #e0e0e0' }}>
                  <h3 style={{ color: '#667eea' }}>{service.name}</h3>
                  <p style={{ color: '#666', fontSize: '0.95rem' }}>{service.description}</p>
                  <div style={{ margin: '15px 0', padding: '10px', background: '#f5f5f5', borderRadius: '4px' }}>
                    <p><strong>üí∞ Pricing:</strong></p>
                    {service.pricing.hourly && <p>  ‚Çπ{service.pricing.hourly}/hour</p>}
                    {service.pricing.fullEvent && <p>  ‚Çπ{service.pricing.fullEvent}/full event</p>}
                  </div>
                  <p><strong>üìç Available Locations:</strong> {service.serviceLocations?.join(', ') || 'Not specified'}</p>
                  <p style={{ fontSize: '0.9rem', color: '#666' }}><strong>Provider:</strong> {service.provider?.name || 'Professional Provider'}</p>
                  <button style={{ marginTop: '15px', width: '100%', padding: '10px', background: '#667eea', color: 'white', border: 'none', borderRadius: '6px', cursor: 'pointer', fontWeight: '600' }}>
                    Add to Event
                  </button>
                </div>
              ))}
            </div>
          )}
        </div>
      )}
    </div>
  );
}

export default CustomerDashboard;
