import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './VenueOwnerDashboard.css';

function VenueOwnerDashboard() {
  const [venues, setVenues] = useState([]);
  const [showForm, setShowForm] = useState(false);
  const [loading, setLoading] = useState(false);
  const [images, setImages] = useState([]);
  const [formData, setFormData] = useState({
    name: '',
    location: '',
    capacity: '',
    pricePerDay: '',
    description: '',
    facilities: '',
    cateringOptions: ''
  });

  const userId = localStorage.getItem('userId');

  // Handle form input changes
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  // Handle image selection
  const handleImageSelect = (e) => {
    const files = Array.from(e.target.files);
    const imagePromises = files.map(file => {
      return new Promise((resolve) => {
        const reader = new FileReader();
        reader.onloadend = () => {
          resolve(reader.result); // Base64 string
        };
        reader.readAsDataURL(file);
      });
    });

    Promise.all(imagePromises).then(base64Images => {
      setImages([...images, ...base64Images]);
    });
  };

  // Remove image from preview
  const removeImage = (index) => {
    setImages(images.filter((_, i) => i !== index));
  };

  // Add venue
  const handleAddVenue = async (e) => {
    e.preventDefault();
    setLoading(true);

    try {
      // Validate required fields
      if (!formData.name || !formData.location || !formData.capacity || !formData.pricePerDay) {
        alert('Please fill all required fields');
        setLoading(false);
        return;
      }

      if (images.length === 0) {
        alert('Please upload at least one image');
        setLoading(false);
        return;
      }

      const venueData = {
        owner: userId,
        name: formData.name,
        location: formData.location,
        capacity: parseInt(formData.capacity),
        pricePerDay: parseInt(formData.pricePerDay),
        description: formData.description,
        images: images,
        facilities: formData.facilities ? formData.facilities.split(',').map(f => f.trim()) : [],
        cateringOptions: formData.cateringOptions ? formData.cateringOptions.split(',').map(c => c.trim()) : [],
        ratings: 0,
        reviews: []
      };

      const response = await axios.post('http://localhost:5000/api/venues/', venueData, {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
      });

      setVenues([...venues, response.data]);
      setFormData({
        name: '',
        location: '',
        capacity: '',
        pricePerDay: '',
        description: '',
        facilities: '',
        cateringOptions: ''
      });
      setImages([]);
      setShowForm(false);
      alert('‚úÖ Venue added successfully!');
    } catch (err) {
      console.error('Error adding venue:', err);
      alert('Error adding venue: ' + (err.response?.data?.message || err.message));
    } finally {
      setLoading(false);
    }
  };

  // Fetch venues on mount
  useEffect(() => {
    const fetchVenues = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/venues/search?location=all', {
          headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
        });
        setVenues(response.data);
      } catch (err) {
        console.error('Error fetching venues:', err);
      }
    };
    fetchVenues();
  }, []);

  return (
    <div className="container">
      <div className="venue-dashboard">
        <h1>üèõÔ∏è Manage Your Venues</h1>
        
        {/* Add Venue Button */}
        <div className="dashboard-header">
          <button 
            className="btn-add-venue" 
            onClick={() => setShowForm(!showForm)}
          >
            {showForm ? '‚ùå Cancel' : '‚ûï Add New Venue'}
          </button>
          <p className="venue-count">Total Venues: {venues.length}</p>
        </div>

        {/* Add Venue Form */}
        {showForm && (
          <div className="add-venue-form-container">
            <h2>üìù Add New Venue</h2>
            <form onSubmit={handleAddVenue} className="add-venue-form">
              
              {/* Basic Information */}
              <div className="form-section">
                <h3>Basic Information</h3>
                
                <div className="form-group">
                  <label>Venue Name *</label>
                  <input
                    type="text"
                    name="name"
                    placeholder="e.g., Grand Palace Banquet Hall"
                    value={formData.name}
                    onChange={handleInputChange}
                    required
                    disabled={loading}
                  />
                </div>

                <div className="form-row">
                  <div className="form-group">
                    <label>Location *</label>
                    <input
                      type="text"
                      name="location"
                      placeholder="e.g., Mumbai, Delhi, Bangalore"
                      value={formData.location}
                      onChange={handleInputChange}
                      required
                      disabled={loading}
                    />
                  </div>

                  <div className="form-group">
                    <label>Seating Capacity *</label>
                    <input
                      type="number"
                      name="capacity"
                      placeholder="e.g., 500"
                      value={formData.capacity}
                      onChange={handleInputChange}
                      required
                      disabled={loading}
                    />
                  </div>

                  <div className="form-group">
                    <label>Price per Day/Event *</label>
                    <input
                      type="number"
                      name="pricePerDay"
                      placeholder="e.g., 50000"
                      value={formData.pricePerDay}
                      onChange={handleInputChange}
                      required
                      disabled={loading}
                    />
                  </div>
                </div>

                <div className="form-group">
                  <label>Description</label>
                  <textarea
                    name="description"
                    placeholder="Describe your venue (amenities, ambiance, etc.)"
                    value={formData.description}
                    onChange={handleInputChange}
                    rows="4"
                    disabled={loading}
                  />
                </div>
              </div>

              {/* Facilities & Services */}
              <div className="form-section">
                <h3>Facilities & Services</h3>
                
                <div className="form-group">
                  <label>Facilities (comma-separated)</label>
                  <input
                    type="text"
                    name="facilities"
                    placeholder="e.g., Parking, Wi-Fi, AC, Projector, Sound System"
                    value={formData.facilities}
                    onChange={handleInputChange}
                    disabled={loading}
                  />
                  <small>üìå Separate with commas</small>
                </div>

                <div className="form-group">
                  <label>Catering Options (comma-separated)</label>
                  <input
                    type="text"
                    name="cateringOptions"
                    placeholder="e.g., Vegetarian, Non-Vegetarian, Vegan, Desserts"
                    value={formData.cateringOptions}
                    onChange={handleInputChange}
                    disabled={loading}
                  />
                  <small>üìå Separate with commas</small>
                </div>
              </div>

              {/* Image Upload */}
              <div className="form-section">
                <h3>üñºÔ∏è Upload Venue Images *</h3>
                
                <div className="image-upload-box">
                  <input
                    type="file"
                    multiple
                    accept="image/*"
                    onChange={handleImageSelect}
                    disabled={loading}
                    id="image-upload"
                    className="file-input"
                  />
                  <label htmlFor="image-upload" className="upload-label">
                    üì∏ Click to select images or drag & drop
                  </label>
                </div>

                {/* Image Preview */}
                {images.length > 0 && (
                  <div className="image-preview">
                    <h4>Selected Images ({images.length})</h4>
                    <div className="preview-grid">
                      {images.map((image, index) => (
                        <div key={index} className="preview-item">
                          <img src={image} alt={`Preview ${index + 1}`} />
                          <button
                            type="button"
                            className="remove-btn"
                            onClick={() => removeImage(index)}
                            disabled={loading}
                          >
                            ‚ùå
                          </button>
                        </div>
                      ))}
                    </div>
                  </div>
                )}
              </div>

              {/* Submit Button */}
              <div className="form-actions">
                <button 
                  type="submit" 
                  className="btn-submit"
                  disabled={loading}
                >
                  {loading ? '‚è≥ Adding Venue...' : '‚úÖ Add Venue'}
                </button>
                <button 
                  type="button" 
                  className="btn-cancel"
                  onClick={() => setShowForm(false)}
                  disabled={loading}
                >
                  Cancel
                </button>
              </div>
            </form>
          </div>
        )}

        {/* Venues List */}
        <div className="venues-list">
          <h2>üìã Your Venues</h2>
          {venues.length === 0 ? (
            <div className="no-venues">
              <p>No venues added yet</p>
              <button 
                className="btn-add-venue-secondary"
                onClick={() => setShowForm(true)}
              >
                ‚ûï Add Your First Venue
              </button>
            </div>
          ) : (
            <div className="venues-grid">
              {venues.map(venue => (
                <div key={venue._id} className="venue-card">
                  {/* Venue Image */}
                  <div className="venue-image">
                    {venue.images && venue.images.length > 0 ? (
                      <img src={venue.images[0]} alt={venue.name} />
                    ) : (
                      <div className="no-image">No Image</div>
                    )}
                  </div>

                  {/* Venue Details */}
                  <div className="venue-details">
                    <h3>{venue.name}</h3>
                    
                    <div className="detail-item">
                      <span>üìç Location:</span>
                      <strong>{venue.location}</strong>
                    </div>

                    <div className="detail-item">
                      <span>üë• Capacity:</span>
                      <strong>{venue.capacity} people</strong>
                    </div>

                    <div className="detail-item">
                      <span>üí∞ Price:</span>
                      <strong>‚Çπ{(venue.pricePerDay && !isNaN(parseInt(venue.pricePerDay))) ? parseInt(venue.pricePerDay).toLocaleString() : '0'}/day</strong>
                    </div>

                    {venue.description && (
                      <div className="detail-item">
                        <span>üìù Description:</span>
                        <p>{venue.description}</p>
                      </div>
                    )}

                    {venue.facilities && venue.facilities.length > 0 && (
                      <div className="detail-item">
                        <span>üè¢ Facilities:</span>
                        <div className="tags">
                          {venue.facilities.map((f, i) => (
                            <span key={i} className="tag">{f}</span>
                          ))}
                        </div>
                      </div>
                    )}

                    {venue.cateringOptions && venue.cateringOptions.length > 0 && (
                      <div className="detail-item">
                        <span>üçΩÔ∏è Catering:</span>
                        <div className="tags">
                          {venue.cateringOptions.map((c, i) => (
                            <span key={i} className="tag">{c}</span>
                          ))}
                        </div>
                      </div>
                    )}

                    {venue.images && venue.images.length > 0 && (
                      <div className="detail-item">
                        <span>üñºÔ∏è Images:</span>
                        <strong>{venue.images.length} photos</strong>
                      </div>
                    )}

                    <div className="venue-rating">
                      <span>‚≠ê Rating: {venue.ratings || 'No ratings yet'}</span>
                    </div>
                  </div>

                  {/* Actions */}
                  <div className="venue-actions">
                    <button className="btn-edit">‚úèÔ∏è Edit</button>
                    <button className="btn-delete">üóëÔ∏è Delete</button>
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

export default VenueOwnerDashboard;
