import React from 'react';
import { Link, useNavigate } from 'react-router-dom';
import './Navbar.css';

function Navbar({ isAuthenticated, userRole }) {
  const navigate = useNavigate();
  const userName = localStorage.getItem('userName');

  const handleLogout = () => {
    // Clear all user data from localStorage
    localStorage.removeItem('token');
    localStorage.removeItem('userId');
    localStorage.removeItem('userRole');
    localStorage.removeItem('userName');
    localStorage.removeItem('userEmail');
    
    // Redirect to home
    navigate('/');
    
    // Refresh page to reset state
    window.location.reload();
  };

  return (
    <nav className="navbar">
      <div className="navbar-container">
        <Link to="/" className="navbar-logo">
          üéâ EventHub
        </Link>
        <ul className="nav-menu">
          <li><Link to="/">Home</Link></li>
          {isAuthenticated && (
            <>
              <li><Link to="/venues">üèõÔ∏è Venues</Link></li>
              <li><Link to="/book-venue">üìÖ Book Venue</Link></li>
              {userRole === 'service_provider' && (
                <li><Link to="/manage-services">üéØ Services</Link></li>
              )}
              {userRole === 'venue_owner' && (
                <>
                  <li><Link to="/venue-bookings">üìã All Bookings</Link></li>
                  <li><Link to="/my-bookings">üìä Manage Bookings</Link></li>
                </>
              )}
              <li><Link to="/dashboard">Dashboard</Link></li>
              <li className="user-info">
                <span>üë§ {userName}</span>
              </li>
            </>
          )}
          {!isAuthenticated && (
            <>
              <li><Link to="/book-venue">üìÖ Book Venue</Link></li>
            </>
          )}
          <li>
            {isAuthenticated ? (
              <button className="logout-btn" onClick={handleLogout}>üö™ Logout</button>
            ) : (
              <div className="auth-buttons">
                <Link to="/login" className="login-link">üîê Login</Link>
                <Link to="/register" className="register-link">‚úçÔ∏è Register</Link>
              </div>
            )}
          </li>
        </ul>
      </div>
    </nav>
  );
}

export default Navbar;
