import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import './VenueSearch.css';

function VenueSearch() {
  const [venues, setVenues] = useState([]);
  const [location, setLocation] = useState('');
  const [loading, setLoading] = useState(false);

  const handleSearch = async (e) => {
    e.preventDefault();
    setLoading(true);
    try {
      const response = await axios.get(`http://localhost:5000/api/venues/search?location=${location}`);
      setVenues(response.data);
    } catch (err) {
      console.error('Error fetching venues:', err);
    }
    setLoading(false);
  };

  return (
    <div className="container">
      <h1>Find Venues</h1>
      <form onSubmit={handleSearch} className="search-form">
        <input
          type="text"
          placeholder="Enter location (city, area, or pincode)"
          value={location}
          onChange={(e) => setLocation(e.target.value)}
          required
        />
        <button type="submit">Search</button>
      </form>

      {loading && <p>Loading...</p>}

      <div className="venues-grid">
        {venues.map(venue => (
          <div key={venue._id} className="venue-card">
            <img src={venue.images[0]} alt={venue.name} />
            <h3>{venue.name}</h3>
            <p className="location">üìç {venue.location}</p>
            <p className="rating">‚≠ê {venue.ratings} ratings</p>
            <p className="capacity">üë• Capacity: {venue.capacity} people</p>
            <p className="price">üí∞ ‚Çπ{venue.pricePerDay}/day</p>
            <Link to={`/venue/${venue._id}`} className="btn-primary">
              View Details
            </Link>
          </div>
        ))}
      </div>
    </div>
  );
}

export default VenueSearch;
