import React, { useState } from 'react';
import axios from 'axios';

function Services() {
  const [serviceType, setServiceType] = useState('catering');
  const [services, setServices] = useState([]);
  const [selectedServices, setSelectedServices] = useState([]);

  const handleSearchServices = async () => {
    try {
      const response = await axios.get(`http://localhost:5000/api/services/type/${serviceType}`);
      setServices(response.data);
    } catch (err) {
      console.error('Error fetching services:', err);
    }
  };

  const handleSelectService = (serviceId) => {
    setSelectedServices([...selectedServices, serviceId]);
  };

  return (
    <div className="container">
      <h1>Add Services to Your Event</h1>
      <div style={{ margin: '30px 0' }}>
        <label>Select Service Type:</label>
        <select value={serviceType} onChange={(e) => setServiceType(e.target.value)}>
          <option value="catering">Catering</option>
          <option value="cultural">Culturals / Dance Teams</option>
          <option value="event_manager">Event Managers</option>
          <option value="decoration">Decorations</option>
          <option value="priest">Priests</option>
          <option value="makeup">Makeup Artists</option>
        </select>
        <button onClick={handleSearchServices} className="btn-primary" style={{ marginLeft: '10px' }}>
          Search
        </button>
      </div>

      <div className="services-grid" style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fill, minmax(300px, 1fr))', gap: '20px' }}>
        {services.map(service => (
          <div key={service._id} style={{ background: 'white', padding: '20px', borderRadius: '8px', boxShadow: '0 2px 8px rgba(0,0,0,0.1)' }}>
            <h3>{service.name}</h3>
            <p>{service.description}</p>
            <p><strong>Rating:</strong> ⭐ {service.ratings}</p>
            <p><strong>Pricing:</strong> ₹{service.pricing.hourly}/hour or ₹{service.pricing.fullEvent}/full event</p>
            <p><strong>Available Locations:</strong> {service.serviceLocations.join(', ')}</p>
            <button onClick={() => handleSelectService(service._id)} className="btn-primary">
              Add Service
            </button>
          </div>
        ))}
      </div>

      {selectedServices.length > 0 && (
        <div style={{ marginTop: '30px', background: 'white', padding: '20px', borderRadius: '8px' }}>
          <h3>Selected Services: {selectedServices.length}</h3>
          <button className="btn-primary">Proceed to Checkout</button>
        </div>
      )}
    </div>
  );
}

export default Services;
