import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import './App.css';
import Navbar from './components/Navbar';
import Login from './pages/Login';
import Register from './pages/Register';
import Home from './pages/Home';
import VenueSearch from './pages/VenueSearch';
import VenueDetail from './pages/VenueDetail';
import Services from './pages/Services';
import CustomerDashboard from './pages/CustomerDashboard';
import VenueOwnerDashboard from './pages/VenueOwnerDashboard';
import ServiceProviderDashboard from './pages/ServiceProviderDashboard';
import VenueBooking from './pages/VenueBooking';

function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(!!localStorage.getItem('token'));
  const [userRole, setUserRole] = useState(localStorage.getItem('userRole'));

  return (
    <Router>
      <Navbar isAuthenticated={isAuthenticated} userRole={userRole} />
      <Routes>
        <Route path="/" element={<Home />} />
        <Route path="/login" element={<Login setIsAuthenticated={setIsAuthenticated} setUserRole={setUserRole} />} />
        <Route path="/register" element={<Register />} />
        <Route path="/venues" element={isAuthenticated ? <VenueSearch /> : <Navigate to="/login" />} />
        <Route path="/venue/:id" element={isAuthenticated ? <VenueDetail /> : <Navigate to="/login" />} />
        <Route path="/book-venue" element={<VenueBooking />} />
        <Route path="/services" element={isAuthenticated ? <Services /> : <Navigate to="/login" />} />
        <Route path="/manage-services" element={
          userRole === 'service_provider' ? <ServiceProviderDashboard /> : <Navigate to="/login" />
        } />
        <Route path="/dashboard" element={
          userRole === 'customer' ? <CustomerDashboard /> :
          userRole === 'venue_owner' ? <VenueOwnerDashboard /> :
          userRole === 'service_provider' ? <ServiceProviderDashboard /> :
          <Navigate to="/login" />
        } />
      </Routes>
    </Router>
  );
}

export default App;
