import React, { useState, useEffect } from 'react';
import axios from 'axios';

function CustomerDashboard() {
  const [bookings, setBookings] = useState([]);

  useEffect(() => {
    const fetchBookings = async () => {
      try {
        const userId = localStorage.getItem('userId');
        const response = await axios.get(`http://localhost:5000/api/bookings/user/${userId}`);
        setBookings(response.data);
      } catch (err) {
        console.error('Error fetching bookings:', err);
      }
    };
    fetchBookings();
  }, []);

  return (
    <div className="container">
      <h1>My Bookings</h1>
      <div style={{ marginTop: '30px' }}>
        {bookings.length === 0 ? (
          <p>No bookings yet</p>
        ) : (
          bookings.map(booking => (
            <div key={booking._id} style={{ background: 'white', padding: '20px', borderRadius: '8px', marginBottom: '15px', boxShadow: '0 2px 8px rgba(0,0,0,0.1)' }}>
              <h3>Event on {new Date(booking.eventDate).toDateString()}</h3>
              <p><strong>Type:</strong> {booking.eventType}</p>
              <p><strong>Status:</strong> <span style={{ color: booking.status === 'confirmed' ? 'green' : 'orange' }}>{booking.status}</span></p>
              <p><strong>Total Cost:</strong> â‚¹{booking.totalCost}</p>
              <p><strong>Payment Status:</strong> {booking.paymentStatus}</p>
            </div>
          ))
        )}
      </div>
    </div>
  );
}

export default CustomerDashboard;
